import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './RegisterForm.css';

const RegisterForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    phiId: '',
    nic: '',
    address: '',
    email: '',
    phone: '+94',
    officeLocation: '',
    district: '',
    gnDivisions: '',
    password: '',
    confirmPassword: '',
    showPassword: false
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  // Sri Lankan districts
  const districts = [
    'Colombo', 'Gampaha', 'Kalutara', 'Kandy', 'Matale', 'Nuwara Eliya',
    'Galle', 'Matara', 'Hambantota', 'Jaffna', 'Kilinochchi', 'Mannar',
    'Mullaitivu', 'Vavuniya', 'Puttalam', 'Kurunegala', 'Anuradhapura',
    'Polonnaruwa', 'Badulla', 'Monaragala', 'Ratnapura', 'Kegalle', 
    'Trincomalee', 'Batticaloa', 'Ampara'
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const togglePasswordVisibility = () => {
    setFormData(prev => ({
      ...prev,
      showPassword: !prev.showPassword
    }));
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.name.trim()) newErrors.name = 'Name is required';
    if (!formData.phiId.trim()) newErrors.phiId = 'PHI ID is required';
    if (!formData.nic.trim()) newErrors.nic = 'NIC is required';
    if (!formData.address.trim()) newErrors.address = 'Address is required';
    if (!formData.email.trim()) newErrors.email = 'Email is required';
    else if (!/^\S+@\S+\.\S+$/.test(formData.email)) newErrors.email = 'Invalid email format';
    if (!formData.phone.trim() || formData.phone === '+94') newErrors.phone = 'Phone number is required';
    if (!formData.officeLocation.trim()) newErrors.officeLocation = 'Office location is required';
    if (!formData.district) newErrors.district = 'District is required';
    if (!formData.gnDivisions.trim()) newErrors.gnDivisions = 'GN Division is required';
    if (!formData.password) newErrors.password = 'Password is required';
    else if (formData.password.length < 8) newErrors.password = 'Password must be at least 8 characters';
    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      setIsSubmitting(true);
      try {
        // Simulate API call
        await new Promise(resolve => setTimeout(resolve, 1500));
        console.log('Registration data:', formData);
        navigate('/dashboard'); // Redirect after registration
      } catch (error) {
        console.error('Registration error:', error);
      } finally {
        setIsSubmitting(false);
      }
    }
  };

  const handleBack = () => {
    navigate(-1);
  };

  return (
    <div className="register-container">
      <div className="register-card">
        <button onClick={handleBack} className="back-button" disabled={isSubmitting}>
          &larr;
        </button>

        <div className="register-header">
          <h1>Welcome to <span className="brand">SafeTrack</span></h1>
          <p>Enter Valid Details to Continue</p>
        </div>

        <form onSubmit={handleSubmit} noValidate>
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className={errors.name ? 'error' : ''}
              disabled={isSubmitting}
              placeholder="Enter your full name"
            />
            {errors.name && <span className="error-message">{errors.name}</span>}
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="phiId">PHI ID</label>
              <input
                type="text"
                id="phiId"
                name="phiId"
                value={formData.phiId}
                onChange={handleChange}
                className={errors.phiId ? 'error' : ''}
                disabled={isSubmitting}
                placeholder="Enter PHI ID"
              />
              {errors.phiId && <span className="error-message">{errors.phiId}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="nic">NIC</label>
              <input
                type="text"
                id="nic"
                name="nic"
                value={formData.nic}
                onChange={handleChange}
                className={errors.nic ? 'error' : ''}
                disabled={isSubmitting}
                placeholder="Enter NIC number"
              />
              {errors.nic && <span className="error-message">{errors.nic}</span>}
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="address">Personal Address</label>
            <input
              type="text"
              id="address"
              name="address"
              value={formData.address}
              onChange={handleChange}
              className={errors.address ? 'error' : ''}
              disabled={isSubmitting}
              placeholder="Enter your address"
            />
            {errors.address && <span className="error-message">{errors.address}</span>}
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className={errors.email ? 'error' : ''}
                disabled={isSubmitting}
                placeholder="Enter your email"
              />
              {errors.email && <span className="error-message">{errors.email}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="phone">Phone</label>
              <div className="phone-input">
                <span className="country-code">+94</span>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  className={errors.phone ? 'error' : ''}
                  disabled={isSubmitting}
                  placeholder="Enter phone number"
                />
              </div>
              {errors.phone && <span className="error-message">{errors.phone}</span>}
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="officeLocation">Office Location</label>
            <input
              type="text"
              id="officeLocation"
              name="officeLocation"
              value={formData.officeLocation}
              onChange={handleChange}
              className={errors.officeLocation ? 'error' : ''}
              disabled={isSubmitting}
              placeholder="Enter office location"
            />
            {errors.officeLocation && <span className="error-message">{errors.officeLocation}</span>}
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="district">District</label>
              <select
                id="district"
                name="district"
                value={formData.district}
                onChange={handleChange}
                className={errors.district ? 'error' : ''}
                disabled={isSubmitting}
              >
                <option value="">Select District</option>
                {districts.map(district => (
                  <option key={district} value={district}>{district}</option>
                ))}
              </select>
              {errors.district && <span className="error-message">{errors.district}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="gnDivisions">Assigned GN Divisions</label>
              <input
                type="text"
                id="gnDivisions"
                name="gnDivisions"
                value={formData.gnDivisions}
                onChange={handleChange}
                className={errors.gnDivisions ? 'error' : ''}
                disabled={isSubmitting}
                placeholder="Enter GN divisions"
              />
              {errors.gnDivisions && <span className="error-message">{errors.gnDivisions}</span>}
            </div>
          </div>

          <div className="form-row">
            <div className="form-group password-group">
              <label htmlFor="password">Password</label>
              <input
                type={formData.showPassword ? "text" : "password"}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className={errors.password ? 'error' : ''}
                disabled={isSubmitting}
                placeholder="Enter password"
              />
              <button
                type="button"
                className="password-toggle"
                onClick={togglePasswordVisibility}
                disabled={isSubmitting}
              >
                {formData.showPassword ? 'üôà' : 'üëÅ'}
              </button>
              {errors.password && <span className="error-message">{errors.password}</span>}
            </div>

            <div className="form-group password-group">
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input
                type={formData.showPassword ? "text" : "password"}
                id="confirmPassword"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                className={errors.confirmPassword ? 'error' : ''}
                disabled={isSubmitting}
                placeholder="Confirm password"
              />
              <button
                type="button"
                className="password-toggle"
                onClick={togglePasswordVisibility}
                disabled={isSubmitting}
              >
                {formData.showPassword ? 'üôà' : 'üëÅ'}
              </button>
              {errors.confirmPassword && (
                <span className="error-message">{errors.confirmPassword}</span>
              )}
            </div>
          </div>

          <button
            type="submit"
            className="register-button"
            disabled={isSubmitting}
          >
            {isSubmitting ? (
              <>
                <span className="spinner"></span>
                <span className="button-text">Registering...</span>
              </>
            ) : (
              'Register'
            )}
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegisterForm;
